INSERT INTO `news` (`id`, `category_id`, `img_square`, `img_horizontal`, `slug`, `title`, `summary`, `content`, `title_header`, `description_header`, `keyword_header`, `view_count`, `created_date`, `updated_date`)
VALUES
	(1,22,'assets/news/news-1.png','assets/news/news-1-h.png','du-hoc-tieng-han-tai-han-quoc','Du học tiếng','Chi phí trọn gói cho việc đào tạo và làm hồ sơ đi du học tiếng Hàn chỉ với 34 triệu VNĐ. Hơn thế nữa ISSILOO đã hỗ trợ được 100% học sinh có được Visa D4-1 ','','Du học tiếng hàn tại hàn quốc','Đi du học tiếng Hàn là dành cho du học sinh quốc tế đăng ký tham gia khóa học tiếng Hàn tại Hàn Quốc với visa D4-1. Thực tế thì gần như 100% du học sinh Việt Nam sang Hàn du học tiếng Hàn là để tiếp tục chuyển lên học một chuyên ngành hệ cao đẳng hay đại học. Nếu học chuyên ngành hệ cao đẳng thì chỉ cần bạn học đạt topik 2 là tự động chuyển sang được. Còn học chuyên ngành hệ đại học thì phải đạt topik 3 trở lên mới chuyên sang được (Một số chuyên ngành hệ đại học như: CNTT, điện tử, máy móc, luyện thép, đúc nhựa, đúc gang, đúc nhôm, sản xuất khuân mẫu, máy ép thủy lực… thì chỉ cần topik 2).','Du học, du học, tiếng hàn, tại, hàn quốc, korea, du học tiếng hàn, tại hàn quốc, hàn quốc có gì hay, hàn quốc đẹp, du lịch, du lịch hàn quốc',4,'2018-10-31 14:23:40','2018-11-12 01:13:42'),
	(2,3,'assets/news/du-hoc-nghe-han-quoc.jpg','assets/news/du-hoc-nghe-han-quoc.jpg','du-hoc-nghe-han-quoc-visad46','Du học Nghề Hàn Quốc','<p>Nhập nội dung rút gọn...</p>','<p style=\"text-align: justify;\"><strong><span style=\"font-family: Arial, Helvetica, sans-serif; font-size: 14px;\">Du học nghề tại H&agrave;n Quốc l&agrave; chương tr&igrave;nh học r&uacute;t ngắn, c&oacute; thể vừa học vừa đi l&agrave;m th&ecirc;m tăng thu nhập. Trong năm 2018, du học nghề H&agrave;n Quốc sẽ l&agrave; từ kh&oacute;a được g&otilde; t&igrave;m nhiều nhất v&agrave; sẽ l&agrave; một hướng đi mới đầy hứa hẹn.</span></strong></p><p dir=\"ltr\" style=\"line-height: 1.38; margin-top: 0pt; margin-bottom: 0pt; text-align: justify;\">Gần đ&acirc;y khi c&aacute;c bạn x&ocirc;n sao về chương tr&igrave;nh Du học Nghề Visa D4-6&nbsp;</p><p dir=\"ltr\" style=\"line-height: 1.38; margin-top: 0pt; margin-bottom: 0pt; text-align: justify;\">Th&igrave; c&oacute; một chương tr&igrave;nh kh&aacute;c đ&atilde; &acirc;m thầm tồn tại trước đ&oacute; m&agrave; &iacute;t c&aacute;c bạn biết tới đ&oacute; ch&iacute;nh l&agrave; chương tr&igrave;nh Du học Nghề Visa D2-1.&nbsp;</p><p dir=\"ltr\" style=\"line-height: 1.38; margin-top: 0pt; margin-bottom: 0pt; text-align: justify;\">H&ocirc;m nay, ISSILOO c&ugrave;ng bạn t&igrave;m hiểu r&otilde; hơn về 2 chương tr&igrave;nh Du học ng&agrave;y giống v&agrave; kh&aacute;c nhau như thế n&agrave;o nh&eacute;!&nbsp;</p><p dir=\"ltr\" style=\"line-height: 1.38; margin-top: 0pt; margin-bottom: 0pt; text-align: justify;\"><br></p><p dir=\"ltr\" style=\"line-height: 1.38; margin-top: 0pt; margin-bottom: 0pt; text-align: justify;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial; color: rgb(0, 0, 0); background-color: rgb(255, 255, 255); font-weight: 400; font-style: normal; font-variant-ligatures: normal; font-variant-caps: normal; text-decoration: none; vertical-align: baseline; white-space: pre-wrap;\"><img src=\"http://issiloo.edu.vn/assets/upload/images/gallery/issiloo-du-hoc-nghe.jpg\" style=\"width: 770px;\" class=\"fr-fic fr-dib\"></span></span></p><p dir=\"ltr\" style=\"line-height: 1.38; margin-top: 0pt; margin-bottom: 0pt; text-align: justify;\"><br></p><p dir=\"ltr\" style=\"line-height: 1.38; margin-top: 0pt; margin-bottom: 0pt; text-align: justify;\"><span style=\"font-size: 18px;\"><span style=\"color: rgb(44, 130, 201);\"><span style=\"font-family: Arial,Helvetica,sans-serif;\"><br></span></span></span></p><p dir=\"ltr\" style=\"line-height: 1.38; margin-top: 0pt; margin-bottom: 0pt; text-align: justify;\"><span style=\"font-size: 18px;\"><b style=\"font-weight:normal;\"><span style=\"font-family: Arial, Helvetica, sans-serif; color: rgb(44, 130, 201);\">1. V&igrave; sao chương tr&igrave;nh Du học Nghề H&agrave;n Quốc ra đời?</span></b></span></p><p dir=\"ltr\" style=\"line-height: 1.38; margin-top: 0pt; margin-bottom: 0pt; text-align: justify;\"><br></p><p dir=\"ltr\" style=\"line-height: 1.38; margin-top: 0pt; margin-bottom: 0pt; text-align: justify;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial; color: rgb(0, 0, 0); background-color: rgb(255, 255, 255); font-weight: 400; font-style: normal; font-variant-ligatures: normal; font-variant-caps: normal; text-decoration: none; vertical-align: baseline; white-space: pre-wrap;\">Như bạn được biết, nếu đi xuất khẩu lao động, c&aacute;c bạn chỉ được l&agrave;m những c&ocirc;ng việc phổ th&ocirc;ng v&agrave; sau khi hết hạn Visa c&aacute;c bạn phải về nước. Nếu đi du học chuy&ecirc;n ng&agrave;nh, thời gian l&agrave;m việc của bạn cũng bị giới hạn v&agrave; thường c&aacute;c việc c&aacute;c bạn l&agrave;m th&ecirc;m cũng tr&aacute;i với chuy&ecirc;n ng&agrave;nh m&agrave; c&aacute;c bạn đang theo học. Những c&ocirc;ng việc đ&oacute; thường được mang t&iacute;nh chất l&agrave; hỗ trợ th&ecirc;m thu nhập trong qu&aacute; tr&igrave;nh du học, chứ kh&ocirc;ng hề ph&aacute;t triển chuy&ecirc;n m&ocirc;n m&agrave; c&aacute;c bạn đang học. Trong khi đ&oacute; tại H&agrave;n Quốc, họ đang thiếu một nguồn lực kỹ sư l&agrave;nh nghề th&igrave; cả hai diện tr&ecirc;n đều chưa đ&aacute;p ứng được. Đ&oacute; l&agrave; l&iacute; do m&agrave; H&agrave;n Quốc x&uacute;c tiến chương tr&igrave;nh Du học Nghề.&nbsp;</span></span></p><p dir=\"ltr\" style=\"line-height: 1.38; margin-top: 0pt; margin-bottom: 0pt; text-align: justify;\"><br></p><p dir=\"ltr\" style=\"line-height: 1.38; margin-top: 0pt; margin-bottom: 0pt; text-align: justify;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial; color: rgb(0, 0, 0); background-color: rgb(255, 255, 255); font-weight: 400; font-style: normal; font-variant-ligatures: normal; font-variant-caps: normal; text-decoration: none; vertical-align: baseline; white-space: pre-wrap;\">Với mục đ&iacute;ch đ&oacute; chương tr&igrave;nh được thiết kế chuy&ecirc;n về kỹ năng thực h&agrave;nh. Đ&oacute; l&agrave; điểm giống nhau thứ nhất của chương tr&igrave;nh Du học nghề Visa D4-6 v&agrave; Visa D2-1&nbsp;</span></span></p><p dir=\"ltr\" style=\"line-height: 1.38; margin-top: 0pt; margin-bottom: 0pt; text-align: justify;\"><br></p><p dir=\"ltr\" style=\"line-height: 1.38; margin-top: 0pt; margin-bottom: 0pt; text-align: justify;\"><br></p><p dir=\"ltr\" style=\"line-height: 1.38; margin-top: 0pt; margin-bottom: 0pt; text-align: justify;\"><img src=\"http://issiloo.edu.vn/assets/upload/images/gallery/issiloo-ki-ket-hd-voi-truong-Seogang.jpg\" style=\"width: 770px;\" class=\"fr-fic fr-dib\"></p><p dir=\"ltr\" style=\"line-height: 1.38; margin-top: 0pt; margin-bottom: 0pt; text-align: justify;\"><br></p><p dir=\"ltr\" style=\"line-height: 1.38; margin-top: 0pt; margin-bottom: 0pt; text-align: justify;\"><br></p><p dir=\"ltr\" style=\"line-height: 1.38; margin-top: 0pt; margin-bottom: 0pt; text-align: justify;\"><span style=\"color: rgb(44, 130, 201);\"><span style=\"font-size: 18px;\">2<a href=\"http://issiloo.edu.vn/news/du-hoc-nghe-han-quoc-visa-d4-6-la-gi\" rel=\"noopener noreferrer\" target=\"_blank\">. Du học nghề H&agrave;n Quốc Visa D4-6&nbsp;</a>v&agrave; D2-1 l&agrave; g&igrave;?&nbsp;</span></span></p><p dir=\"ltr\" style=\"line-height: 1.38; margin-top: 0pt; margin-bottom: 0pt; text-align: justify;\"><br></p><p dir=\"ltr\" style=\"line-height: 1.38; margin-top: 0pt; margin-bottom: 0pt; text-align: justify;\">Visa D4-6 l&agrave; một h&igrave;nh thức visa mới được đưa v&agrave;o &aacute;p dụng v&agrave;o năm 2017. L&agrave; chương tr&igrave;nh kết hợp đ&agrave;o tạo tiếng v&agrave; chuy&ecirc;n ng&agrave;nh chung với nhau, sau khi tốt nghiệp chương tr&igrave;nh n&agrave;y thường l&agrave; sau 1 năm đ&agrave;o tạo c&aacute;c bạn sẽ nhận được t&iacute;n chỉ nghề do nh&agrave; trường cấp. V&agrave; nếu bạn thi k&igrave; thi quốc gia, th&igrave; sẽ c&oacute; th&ecirc;m chứng chỉ nghề Quốc gia. Nhưng Visa n&agrave;y kh&ocirc;ng được chuyển sang visa D10 ngay, m&agrave; bạn phải học từ 1 đến 2 năm chuy&ecirc;n ng&agrave;nh để chuyển tiếp Visa D4-6 th&agrave;nh visa D2. Thời gian học l&ecirc;n l&agrave; 2 năm nữa. Sau khi tốt nghiệp ra trường th&igrave; bạn được chuyển tiếp visa D10 (xin việc l&agrave;m) rồi mới sang E7 ( kỹ sư chuy&ecirc;n ng&agrave;nh)&nbsp;</p><p dir=\"ltr\" style=\"line-height: 1.38; margin-top: 0pt; margin-bottom: 0pt; text-align: justify;\"><br></p><p dir=\"ltr\" style=\"line-height: 1.38; margin-top: 0pt; margin-bottom: 0pt; text-align: justify;\">Visa D2-1 l&agrave; một chương tr&igrave;nh đ&atilde; c&oacute; từ l&acirc;u cho chương tr&igrave;nh Du học H&agrave;n Quốc, hiện tại đ&atilde; c&oacute; rất nhiều Du học sinh đang học theo chương tr&igrave;nh n&agrave;y, nhưng chưa nhận được ưu &aacute;i của bộ phận truyền th&ocirc;ng trong nước, n&ecirc;n c&aacute;c bạn chưa biết đến nhiều.&nbsp;</p><p dir=\"ltr\" style=\"line-height: 1.38; margin-top: 0pt; margin-bottom: 0pt; text-align: justify;\"><br></p><p dir=\"ltr\" style=\"line-height: 1.38; margin-top: 0pt; margin-bottom: 0pt; text-align: justify;\">Visa D2-1 được cấp cho c&aacute;c bạn du học sinh học chương tr&igrave;nh cao đẳng, trung cấp tại H&agrave;n Quốc. Nếu như truyền thống, c&aacute;c bạn cũng sẽ đi theo dạng visa D4-1( du học tiếng) rồi mới được chuyển tiếp sang Visa chuy&ecirc;n ng&agrave;nh (D2). Nhưng v&igrave; nhu cầu tuyển sinh của một v&agrave;i trường, n&ecirc;n họ được ph&eacute;p tuyển thẳng cho du học sinh đi thẳng qua D2-1 m&agrave; kh&ocirc;ng cần th&ocirc;ng qua Visa D4-1. Điều kiện tuyển sinh phụ thuộc v&agrave;o từng trường kh&aacute;c nhau chứ kh&ocirc;ng phải theo quy định của Bộ Thư Ph&aacute;p như chương tr&igrave;nh D4-6. Tuy nhi&ecirc;n, để đ&aacute;nh gi&aacute; 1 trường c&oacute; đ&agrave;o tạo c&oacute; tốt hay kh&ocirc;ng, th&igrave; c&aacute;c bạn cũng n&ecirc;n xem điều kiện x&eacute;t tuyển c&oacute; đ&ograve;i hỏi năng lực tiếng H&agrave;n như thế bạn nh&eacute;, v&igrave; thường bạn qua học sẽ học chuy&ecirc;n ng&agrave;nh lu&ocirc;n n&ecirc;n cũng biết tiếng cơ bản, chứ kh&ocirc;ng biết g&igrave;, th&igrave; kh&ocirc;ng biết qua đ&oacute; học h&agrave;nh như thế n&agrave;o.&nbsp;</p><p dir=\"ltr\" style=\"line-height: 1.38; margin-top: 0pt; margin-bottom: 0pt; text-align: justify;\"><br></p><p dir=\"ltr\" style=\"line-height: 1.38; margin-top: 0pt; margin-bottom: 0pt; text-align: justify;\">Đ&acirc;y l&agrave; điểm lưu &yacute; cho từng loại visa Du học Nghề m&agrave; bạn cần ch&uacute; &yacute; nh&eacute;!&nbsp;</p><p dir=\"ltr\" style=\"line-height: 1.38; margin-top: 0pt; margin-bottom: 0pt; text-align: justify;\"><span style=\"color: rgb(44, 130, 201);\"><span style=\"font-size: 18px; font-family: Arial; background-color: transparent; font-weight: 400; font-style: normal; font-variant: normal; text-decoration: none; vertical-align: baseline; white-space: pre-wrap;\"><br></span></span></p><p dir=\"ltr\" style=\"line-height: 1.38; margin-top: 0pt; margin-bottom: 0pt; text-align: justify;\"><span style=\"font-size: 18px;\"><span style=\"color: rgb(44, 130, 201);\">3</span><a href=\"http://issiloo.edu.vn/news/uu-diem-cua-chuong-trinh-du-hoc-nghe-han-quoc\" rel=\"noopener noreferrer\" target=\"_blank\"><span style=\"color: rgb(44, 130, 201);\">. Ưu điểm của chương tr&igrave;nh Du học Nghề tại H&agrave;n Quốc l&agrave; g&igrave;?</span>&nbsp;</a></span></p><p dir=\"ltr\" style=\"line-height: 1.38; margin-top: 0pt; margin-bottom: 0pt; text-align: justify;\">- Thời gian đ&agrave;o tạo ngắn</p><p dir=\"ltr\" style=\"line-height: 1.38; margin-top: 0pt; margin-bottom: 0pt; text-align: justify;\">D4-6 + D2 &nbsp;: tổng thời gian tối thiểu l&agrave; 3 năm&nbsp;</p><p dir=\"ltr\" style=\"line-height: 1.38; margin-top: 0pt; margin-bottom: 0pt; text-align: justify;\">D2-1: tổng thời gian tối thiểu &nbsp;l&agrave; 2 năm&nbsp;</p><p dir=\"ltr\" style=\"line-height: 1.38; margin-top: 0pt; margin-bottom: 0pt; text-align: justify;\">- Điều kiện tuyển sinh mở rộng, ch&uacute; trọng nhiều v&agrave;o kỹ năng tiếng H&agrave;n của du học sinh&nbsp;</p><p dir=\"ltr\" style=\"line-height: 1.38; margin-top: 0pt; margin-bottom: 0pt; text-align: justify;\">- Được hỗ trợ đi thực tập c&oacute; lương&nbsp;</p><p dir=\"ltr\" style=\"line-height: 1.38; margin-top: 0pt; margin-bottom: 0pt; text-align: justify;\"><br></p><p dir=\"ltr\" style=\"line-height: 1.38; margin-top: 0pt; margin-bottom: 0pt; text-align: justify;\"><span style=\"font-family: Arial, Helvetica, sans-serif; font-size: 14px; color: rgb(184, 49, 47);\">=&gt;&gt;&gt; Xem th&ecirc;m: Video giới thiệu ư</span><span style=\"color: rgb(184, 49, 47);\"><a href=\"https://www.youtube.com/watch?v=3VQlZpSDGaM&t=55s\" rel=\"noopener noreferrer\" target=\"_blank\"><span style=\"font-family: Arial, Helvetica, sans-serif; font-size: 14px;\">u điểm của chương tr&igrave;nh Du học Nghề H&agrave;n Quốc</span></a></span></p><p dir=\"ltr\" style=\"line-height: 1.38; margin-top: 0pt; margin-bottom: 0pt; text-align: justify;\"><br></p><p dir=\"ltr\" style=\"line-height: 1.38; margin-top: 0pt; margin-bottom: 0pt; text-align: justify;\"><span style=\"font-size:12pt;font-family:Arial;color:#000000;background-color:#ffffff;font-weight:400;font-style:normal;font-variant:normal;text-decoration:none;vertical-align:baseline;white-space:pre-wrap;\"><br class=\"kix-line-break\"></span><span style=\"color: rgb(44, 130, 201);\"><span style=\"font-size: 18px;\">4</span></span><a href=\"http://issiloo.edu.vn/news/dieu-kien-tham-gia-chuong-trinh-du-hoc-nghe-han-quoc\" rel=\"noopener noreferrer\" target=\"_blank\"><span style=\"color: rgb(44, 130, 201);\"><span style=\"font-size: 18px;\">. Điều kiện tham gia chương tr&igrave;nh Du học Nghề tại H&agrave;n Quốc</span></span>&nbsp;</a></p><p dir=\"ltr\" style=\"line-height: 1.38; margin-top: 0pt; margin-bottom: 0pt; text-align: justify;\">Với Visa D4-6&nbsp;</p><p dir=\"ltr\" style=\"line-height: 1.38; margin-top: 0pt; margin-bottom: 0pt; text-align: justify;\">- Tốt nghiệp THPT&nbsp;</p><p dir=\"ltr\" style=\"line-height: 1.38; margin-top: 0pt; margin-bottom: 0pt; text-align: justify;\">- Tuổi từ 18 - 30&nbsp;</p><p dir=\"ltr\" style=\"line-height: 1.38; margin-top: 0pt; margin-bottom: 0pt; text-align: justify;\">- C&oacute; TOPIK 2 hoặc KLAT 2&nbsp;</p><p dir=\"ltr\" style=\"line-height: 1.38; margin-top: 0pt; margin-bottom: 0pt; text-align: justify;\"><br></p><p dir=\"ltr\" style=\"line-height: 1.38; margin-top: 0pt; margin-bottom: 0pt; text-align: justify;\">Với Visa D2-1, điều kiện tuyển sinh phụ thuộc v&agrave;o từng trường.&nbsp;</p><p dir=\"ltr\" style=\"line-height: 1.38; margin-top: 0pt; margin-bottom: 0pt; text-align: justify;\">V&iacute; dụ với trường <a class=\"fr-strong\" href=\"http://issiloo.edu.vn/news/truong-cao-dang-khoa-hoc-ky-thuat-busan-truong-dao-tao-nghe-danh-tieng\">Khoa học Kỹ Thuật Busan (BIST)&nbsp;</a></p><p dir=\"ltr\" style=\"line-height: 1.38; margin-top: 0pt; margin-bottom: 0pt; text-align: justify;\">Điều kiện tuyển sinh&nbsp;</p><p dir=\"ltr\" style=\"line-height: 1.38; margin-top: 0pt; margin-bottom: 0pt; text-align: justify;\">- Tốt nghiệp THPT&nbsp;</p><p dir=\"ltr\" style=\"line-height: 1.38; margin-top: 0pt; margin-bottom: 0pt; text-align: justify;\">- Tuổi từ 18 - 30&nbsp;</p><p dir=\"ltr\" style=\"line-height: 1.38; margin-top: 0pt; margin-bottom: 0pt; text-align: justify;\">- C&oacute; TOPIK 2, hoặc KLAT 2 v&agrave; vượt qua k&igrave; thi s&aacute;t hạch năng lực tiếng của Trường <a class=\"fr-strong\" href=\"http://issiloo.edu.vn/news/truong-cao-dang-khoa-hoc-ky-thuat-busan-truong-dao-tao-nghe-danh-tieng\">Khoa học Kỹ Thuật Busan (BIST)&nbsp;</a></p><p dir=\"ltr\" style=\"line-height: 1.38; margin-top: 0pt; margin-bottom: 0pt; text-align: justify;\"><a href=\"https://www.youtube.com/watch?v=3VQlZpSDGaM&t=136s\" rel=\"noopener noreferrer\" target=\"_blank\"><span style=\"color: rgb(44, 130, 201);\"><span style=\"font-size: 18px;\"><span style=\"font-family: Arial; background-color: rgb(255, 255, 255); font-weight: 400; font-style: normal; font-variant-ligatures: normal; font-variant-caps: normal; text-decoration: none; vertical-align: baseline; white-space: pre-wrap;\"><br class=\"kix-line-break\"></span></span></span></a></p><p dir=\"ltr\" style=\"line-height: 1.38; margin-top: 0pt; margin-bottom: 0pt; text-align: justify;\"><a href=\"https://www.youtube.com/watch?v=3VQlZpSDGaM&t=136s\" rel=\"noopener noreferrer\" target=\"_blank\"><span style=\"font-size: 18px; color: rgb(44, 130, 201);\">5. Lộ tr&igrave;nh chuyển tiếp visa của chương tr&igrave;nh Du học Nghề H&agrave;n Quốc&nbsp;</span></a></p><p dir=\"ltr\" style=\"line-height: 1.38; margin-top: 0pt; margin-bottom: 0pt; text-align: justify;\"><span style=\"font-size: 14px; font-family: Arial, Helvetica, sans-serif;\"><br></span></p><p dir=\"ltr\" style=\"line-height: 1.38; margin-top: 0pt; margin-bottom: 0pt; text-align: justify;\"><span style=\"font-size: 14px; font-family: Arial, Helvetica, sans-serif;\">Lộ tr&igrave;nh chuyển tiếp visa như sau: <span style=\"font-size: 14px;\"><span style=\"font-family: Arial; color: rgb(0, 0, 0); background-color: rgb(255, 255, 255); font-weight: 400; font-style: normal; font-variant-ligatures: normal; font-variant-caps: normal; text-decoration: none; vertical-align: baseline; white-space: pre-wrap;\"><strong>(nhấp v&agrave;o ti&ecirc;u đề để xem th&ecirc;m)</strong></span></span></span></p><p dir=\"ltr\" style=\"line-height: 1.38; margin-top: 0pt; margin-bottom: 0pt; text-align: justify;\"><span style=\"font-size: 14px; font-family: Arial, Helvetica, sans-serif;\">&nbsp;</span></p><p dir=\"ltr\" style=\"line-height: 1.38; margin-top: 0pt; margin-bottom: 0pt; text-align: justify;\"><span style=\"font-size: 14px; font-family: Arial, Helvetica, sans-serif;\"><span style=\"color: rgb(184, 49, 47);\"><span style=\"font-size: 14px; font-family: Arial, Helvetica, sans-serif;\"><a href=\"https://www.youtube.com/results?search_query=du+h%E1%BB%8Dc+ngh%E1%BB%81+h%C3%A0n+qu%E1%BB%91c\" rel=\"noopener noreferrer\" target=\"_blank\">=&gt;&gt;&gt; Xem th&ecirc;m: Video giơi thiệu lộ tr&igrave;nh chuyển tiếp visa</a></span></span></span></p><p dir=\"ltr\" style=\"line-height: 1.38; margin-top: 0pt; margin-bottom: 0pt; text-align: justify;\"><br></p><p dir=\"ltr\" style=\"line-height: 1.38; margin-top: 0pt; margin-bottom: 0pt; text-align: justify;\"><br></p><p><img src=\"http://issiloo.edu.vn/assets/upload/images/gallery/issiloo-lo-trinh-chuyen-tiep-visa.jpg\" style=\"width: 770px;\" class=\"fr-fic fr-dib\"></p><p><br></p><p><em><strong>V&agrave; nếu như vậy, bạn cũng c&oacute; thể thấy được Du học Nghề Visa D2-1 sẽ bỏ qua được bước chuyển tiếp từ D4-6 , m&agrave; l&ecirc;n thẳng D2-1 sau đ&oacute; sang D10 v&agrave; E7 tương tự như visa D4-6</strong></em>&nbsp;</p><p dir=\"ltr\" style=\"line-height: 1.38; margin-top: 0pt; margin-bottom: 0pt; text-align: justify;\"><a href=\"http://issiloo.edu.vn/news/du-hoc-nghe-han-quoc-visa-d4-6-cung-issiloo\" rel=\"noopener noreferrer\" target=\"_blank\"><span style=\"font-size: 18px;\"><br></span></a></p><p dir=\"ltr\" style=\"line-height: 1.38; margin-top: 0pt; margin-bottom: 0pt; text-align: justify;\"><span style=\"color: rgb(44, 130, 201);\"><span style=\"font-size: 18px;\">6<a href=\"http://issiloo.edu.vn/news/du-hoc-nghe-han-quoc-visa-d4-6-cung-issiloo\" rel=\"noopener noreferrer\" target=\"_blank\">. Du học nghề H&agrave;n Quốc c&ugrave;ng ISSILOO</a></span></span></p><p dir=\"ltr\" style=\"line-height: 1.38; margin-top: 0pt; margin-bottom: 0pt; text-align: justify;\"><br></p><p dir=\"ltr\" style=\"line-height: 1.38; margin-top: 0pt; margin-bottom: 0pt; text-align: justify;\"><br></p><p dir=\"ltr\" style=\"line-height: 1.38; margin-top: 0pt; margin-bottom: 0pt; text-align: justify;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial; color: rgb(0, 0, 0); background-color: rgb(255, 255, 255); font-weight: 700; font-style: normal; font-variant-ligatures: normal; font-variant-caps: normal; text-decoration: none; vertical-align: baseline; white-space: pre-wrap;\">Lợi &iacute;ch khi lựa chọn Du học Nghề H&agrave;n Quốc tại ISSILOO&nbsp;</span></span></p><p dir=\"ltr\" style=\"line-height: 1.38; margin-top: 0pt; margin-bottom: 0pt; text-align: justify;\"><br></p><p dir=\"ltr\" style=\"line-height: 1.38; margin-top: 0pt; margin-bottom: 0pt; text-align: justify;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial; color: rgb(0, 0, 0); background-color: rgb(255, 255, 255); font-style: normal; font-variant-ligatures: normal; font-variant-caps: normal; text-decoration: none; vertical-align: baseline; white-space: pre-wrap;\">- Thứ nhất, ISSILOO đ&atilde; hợp t&aacute;c với trường <a class=\"fr-strong\" href=\"http://issiloo.edu.vn/news/truong-cao-dang-khoa-hoc-ky-thuat-busan-truong-dao-tao-nghe-danh-tieng\">Khoa học Kỹ Thuật Busan (BIST)&nbsp;</a> n&ecirc;n học vi&ecirc;n ho&agrave;n to&agrave;n y&ecirc;n t&acirc;m bởi kh&ocirc;ng gặp phải cứ kh&oacute; khăn hay trở ngại n&agrave;o về vấn đề hồ sơ, thủ tục.&nbsp;</span></span></p><p dir=\"ltr\" style=\"line-height: 1.38; margin-top: 0pt; margin-bottom: 0pt; text-align: justify;\"><br></p><p dir=\"ltr\" style=\"line-height: 1.38; margin-top: 0pt; margin-bottom: 0pt; text-align: justify;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial; color: rgb(0, 0, 0); background-color: rgb(255, 255, 255); font-style: normal; font-variant-ligatures: normal; font-variant-caps: normal; text-decoration: none; vertical-align: baseline; white-space: pre-wrap;\">- Thứ hai, ISSILOO c&oacute; bộ phận đ&agrave;o tạo tiếng H&agrave;n chuy&ecirc;n nghiệp v&agrave; tận tậm, gi&uacute;p bạn nhanh ch&oacute;ng đ&aacute;p ứng đủ tr&igrave;nh độ để tham gia chương tr&igrave;nh&nbsp;</span></span></p><p dir=\"ltr\" style=\"line-height: 1.38; margin-top: 0pt; margin-bottom: 0pt; text-align: justify;\"><br></p><p dir=\"ltr\" style=\"line-height: 1.38; margin-top: 0pt; margin-bottom: 0pt; text-align: justify;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial; color: rgb(0, 0, 0); background-color: rgb(255, 255, 255); font-style: normal; font-variant-ligatures: normal; font-variant-caps: normal; text-decoration: none; vertical-align: baseline; white-space: pre-wrap;\">- Thứ ba, ISSILOO c&oacute; bộ phận đại diện tại H&agrave;n Quốc, n&ecirc;n c&oacute; thể hỗ trợ c&aacute;c bạn tốt nhất khi c&aacute;c bạn học tập tại H&agrave;n Quốc. &nbsp;</span></span></p><p dir=\"ltr\" style=\"line-height: 1.38; margin-top: 0pt; margin-bottom: 0pt; text-align: justify;\"><br></p><p dir=\"ltr\" style=\"line-height: 1.38; margin-top: 0pt; margin-bottom: 0pt; text-align: justify;\"><span style=\"font-family: Arial, Helvetica, sans-serif; font-size: 18px; color: rgb(44, 130, 201);\"><br></span></p><p dir=\"ltr\" style=\"line-height: 1.38; margin-top: 0pt; margin-bottom: 0pt; text-align: justify;\"><span style=\"font-size: 18px;\"><span style=\"font-family: Arial, Helvetica, sans-serif; color: rgb(44, 130, 201); background-color: rgb(255, 255, 255); font-style: normal; font-variant-ligatures: normal; font-variant-caps: normal; text-decoration: none; vertical-align: baseline; white-space: pre-wrap;\"><a href=\"http://issiloo.edu.vn/news/gioi-thieu-ve-truong-dao-tao-nghe-seogang\" rel=\"noopener noreferrer\" target=\"_blank\">7. Giới thiệu về trường&nbsp;</a>Khoa học Kỹ Thuật Busan (BIST)&nbsp;</span></span></p><p>Xem th&ecirc;m: <a class=\"fr-strong\" href=\"http://issiloo.edu.vn/news/truong-cao-dang-khoa-hoc-ky-thuat-busan-truong-dao-tao-nghe-danh-tieng \">Trường Khoa học Kỹ Thuật Busan (BIST) - trường Đ&agrave;o Tạo Nghề danh tiếng</a></p><p>Li&ecirc;n hệ ngay để được tư vấn chương tr&igrave;nh&nbsp;</p><p><span style=\"color: rgb(41, 105, 176);\"><strong>ISSILOO KOREAN CENTER</strong><br>Đ&agrave;o tạo H&agrave;n Ngữ - Tư Vấn Du học H&agrave;n<br>Lầu 5, T&ograve;a nh&agrave; mới, CĐ Kỹ Nghệ 2, 502 Đỗ Xu&acirc;n Hợp, Quận 9, Hồ Ch&iacute; Minh<br>Hotline/Zalo: 0901 879 877<br>WEB: <a href=\"http://www.issiloo.edu.vn/\"></a><a href=\"http://www.issiloo.edu.vn\">http://www.issiloo.edu.vn</a><br>Facebook: <a href=\"http://fb.com/issiloo.edu.vn\">fb.com/issiloo.edu.vn</a></span></p>','Du học Nghề Hàn Quốc Visa D4-6','Du học nghề Hàn Quốc - Chương trình học vừa học vừa làm. Cơ hội trở thành kỹ sư lành nghề tại Hàn Quốc, du học nghề, vừa học vừa làm, chi phí thấp, trung tâm tư vấn du học hàn quốc uy tín ở tphcm, không cọc chống trốn, miễn phí ktx, miễn phí đào tạo tiếng','trung tâm Hàn ngữ quận 9, Du học Nghề Hàn Quốc, Du học, Hàn Quốc, Visa D46, visa D4-6 là gì?, Visa E7 , du học nghề ở đâu, du học nghề học những ngành nào, chi phí du học nghề bao nhiêu, du học nghề Hàn Quốc uy tín, du học nghề uy tín, chất lượng, du học nghề Hàn quốc có khó không, điều kiện du học nghề là gì, tại sao nên du học nghề, ưu điểm du học nghề Hàn quốc là gì, du học nghề học mấy năm, du học nghề có kiếm được nhiều tiền không, du học nghề Hàn quốc có khó không',31,'2018-10-31 14:23:40','2018-11-11 17:20:09'),
	(3,2,'assets/news/news-3.png','assets/news/news-3-h.png','du-hoc-nghe-han-quoc-visad46','Du học Nghề Hàn Quốc','Chi phí trọn gói cho việc đào tạo và làm hồ sơ đi du học tiếng Hàn chỉ với 34 triệu VNĐ. Hơn thế nữa ISSILOO đã hỗ trợ được 100% học sinh có được Visa D4-1 ','','Du học Nghề Hàn Quốc Visa D4-6','Du học nghề Hàn Quốc - Chương trình học vừa học vừa làm. Cơ hội trở thành kỹ sư lành nghề tại Hàn Quốc, du học nghề, vừa học vừa làm, chi phí thấp, trung tâm tư vấn du học hàn quốc uy tín ở tphcm, không cọc chống trốn, miễn phí ktx, miễn phí đào tạo tiếng','Du học, du học, tiếng hàn, tại, hàn quốc, korea, du học tiếng hàn, tại hàn quốc, hàn quốc có gì hay, hàn quốc đẹp, du lịch, du lịch hàn quốc',11,'2018-10-31 14:23:40','2018-10-31 14:24:39'),
	(4,2,'assets/news/news-4.png','assets/news/news-4-h.png','tieng-han-so-cap','Tiếng hàn sơ cấp','Chi phí trọn gói cho việc đào tạo và làm hồ sơ đi du học tiếng Hàn chỉ với 34 triệu VNĐ. Hơn thế nữa ISSILOO đã hỗ trợ được 100% học sinh có được Visa D4-1 ','','Du học tiếng hàn tại hàn quốc','Đi du học tiếng Hàn là dành cho du học sinh quốc tế đăng ký tham gia khóa học tiếng Hàn tại Hàn Quốc với visa D4-1. Thực tế thì gần như 100% du học sinh Việt Nam sang Hàn du học tiếng Hàn là để tiếp tục chuyển lên học một chuyên ngành hệ cao đẳng hay đại học. Nếu học chuyên ngành hệ cao đẳng thì chỉ cần bạn học đạt topik 2 là tự động chuyển sang được. Còn học chuyên ngành hệ đại học thì phải đạt topik 3 trở lên mới chuyên sang được (Một số chuyên ngành hệ đại học như: CNTT, điện tử, máy móc, luyện thép, đúc nhựa, đúc gang, đúc nhôm, sản xuất khuân mẫu, máy ép thủy lực… thì chỉ cần topik 2).','Du học, du học, tiếng hàn, tại, hàn quốc, korea, du học tiếng hàn, tại hàn quốc, hàn quốc có gì hay, hàn quốc đẹp, du lịch, du lịch hàn quốc',24,'2018-10-31 14:23:40','2018-11-10 04:54:44'),
	(5,2,'assets/news/news-5.png','assets/news/news-5-h.png','tieng-han-trung-cap','Tiếng hàn trung cấp','Chi phí trọn gói cho việc đào tạo và làm hồ sơ đi du học tiếng Hàn chỉ với 34 triệu VNĐ. Hơn thế nữa ISSILOO đã hỗ trợ được 100% học sinh có được Visa D4-1 ','','Du học tiếng hàn tại hàn quốc','Đi du học tiếng Hàn là dành cho du học sinh quốc tế đăng ký tham gia khóa học tiếng Hàn tại Hàn Quốc với visa D4-1. Thực tế thì gần như 100% du học sinh Việt Nam sang Hàn du học tiếng Hàn là để tiếp tục chuyển lên học một chuyên ngành hệ cao đẳng hay đại học. Nếu học chuyên ngành hệ cao đẳng thì chỉ cần bạn học đạt topik 2 là tự động chuyển sang được. Còn học chuyên ngành hệ đại học thì phải đạt topik 3 trở lên mới chuyên sang được (Một số chuyên ngành hệ đại học như: CNTT, điện tử, máy móc, luyện thép, đúc nhựa, đúc gang, đúc nhôm, sản xuất khuân mẫu, máy ép thủy lực… thì chỉ cần topik 2).','Du học, du học, tiếng hàn, tại, hàn quốc, korea, du học tiếng hàn, tại hàn quốc, hàn quốc có gì hay, hàn quốc đẹp, du lịch, du lịch hàn quốc',12,'2018-10-31 14:23:40','2018-10-31 14:24:43'),
